import React, { useState } from 'react';
import ChatMessage from './ChatMessage';

function ChatInterface({ roomImage, children }) {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleSendMessage = async () => {
    if (!inputValue.trim()) {
      return; // Exit if the input is only whitespace or empty.
    }

    const userMessage = { sender: 'user', content: inputValue };
    setMessages(prevMessages => [...prevMessages, userMessage]);

    try {
      const response = await fetch("/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ message: inputValue })
      });

      const rawResponse = await response.text();
      console.log("Server Response:", rawResponse);

      const data = JSON.parse(rawResponse);

      const gpt4Response = { sender: 'gpt-4', content: data.message };
      setMessages(prevMessages => [...prevMessages, gpt4Response]);

    } catch (error) {
      console.error("Error calling server:", error);
    }

    setInputValue('');
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', fontFamily: 'Roboto, sans-serif', display: 'flex', flexDirection: 'column', height: '100vh' }}>

      {/* Black title bar */}
      <div style={{ backgroundColor: 'black', height: '60px', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', fontSize: '24px' }}>
        Title
      </div>

      <img src="/decorAItor_logo.png" alt="Logo" style={{ position: 'absolute', top: '10px', left: '10px', height: '50px', zIndex: 1 }} />
      {roomImage && <img src={roomImage} alt="Room View" style={{ width: '100%', marginBottom: '20px', fontFamily: 'Roboto, sans-serif' }} />}
      {children}
      <div className="chat-window" style={{ flex: 1, border: '1px solid #ccc', padding: '20px', maxHeight: '400px', overflowY: 'scroll', borderRadius: '5px', backgroundColor: '#f4f4f4', fontFamily: 'Roboto, sans-serif' }}>
        {messages.map((msg, index) => (
          <ChatMessage key={index} message={msg} />
        ))}
      </div>
      <div className="chat-input" style={{ marginTop: '20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontFamily: 'Roboto, sans-serif' }}>
        <input
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Give me design ideas"
          style={{ width: '80%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc', fontFamily: 'Roboto, sans-serif' }}
        />
        <button 
          className="robotoFont"
          onClick={handleSendMessage} 
          style={{ 
            width: '18%', 
            padding: '10px', 
            borderRadius: '5px', 
            backgroundColor: 'black', 
            color: 'white', 
            border: 'none', 
            cursor: 'pointer'
          }}>
          Design
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
